import React, { useEffect, useState } from "react";
import "./busca.css";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Table from "react-bootstrap/Table";
import axios from "axios";

const Busca = () => {
  const [search, setSearch] = useState("");
  const [text, setText] = useState("");
  const [cars, setCars] = useState([]);

  const updateSearch = (value) => {
    setText(value);
  };

  const getCars = async () => {
    axios
      .get(`http://localhost:3001/list-cars/${text}`)
      .then((res) => {
        console.log(res.data.data.userCars);
        setCars(res.data.data.userCars);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <>
      <header>
        <h2> Encontre os veiculos de um usuário </h2>
      </header>
      <main>
        <div className="div-form">
          <Form className="d-flex mx-5">
            <Form.Control
              type="search"
              placeholder="Digite o Nome"
              aria-label="Search"
              onChange={(e) => updateSearch(e.target.value)}
            />
            <Button variant="success" onClick={() => getCars()}>
              Buscar
            </Button>
          </Form>
          {cars.map((item, key) => (
            <div className="list-cars">
              //{" "}
              <tr key={key}>
                // <td>{item.ano}</td>;<td>{item.marca}</td>;
                <td>{item.modelo}</td>
                //{" "}
              </tr>
            </div>
          ))}
        </div>
      </main>
    </>
  );
};

export default Busca;
