import React, { useEffect, useState } from "react";
import "./busca.css";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import axios from "axios";
const Busca = () => {
  useEffect(() => {
    axios.get(`http://localhost:3001/list-cars/:${text}`);
  }, [text]);
  const [search, setSearch] = useState("");
  const [text, setText] = useState("");
  const [cars, setCars] = useState([{}]);

  const updateSearch = (value) => {
    setText(value);
  };

  const handleClickSearch = () => {
    setSearch(text);
  };

  const getCars = async () => {
    const response = await axios.get(
      `http://localhost:3001/list-cars/:${text}`
    );
    const carsList = await response.json();
    setCars(carsList);
  };

  return (
    <>
      <header>
        <h2> Encontre os veiculos de um usuário </h2>
      </header>
      <main>
        <div>
          <Form className="d-flex mx-5">
            <Form.Control
              type="search"
              placeholder="Digite o Nome"
              aria-label="Search"
              onChange={(e) => updateSearch(e.target.value)}
            />
            <Button variant="success" onClick={() => getCars}>
              Buscar
            </Button>
          </Form>
        </div>
        <div className="div-cars">
          <tr>
            {cars.map((item, key) => {
              <td>
                <h4>{item.marca}</h4>
                <h4>{item.modelo}</h4>
                <h4>{item.ano}</h4>
              </td>;
            })}
          </tr>
        </div>
      </main>
    </>
  );
};

export default Busca;
